import React, { useContext, useState } from 'react'
import { ItemProviderContext } from "../../context.js/itemProvider";

export const Item = (props) => {
    const { items, getAllItems, newItem, editItem, deleteItem } = useContext(
        ItemProviderContext
      );
    const { item, index } = props

    const [toggle, setToggle ] = useState(true)

    const submitChanges = (e, item) => {
        e.preventDefault();
        editItem(item._id);
      };

      const remove = (item) => {
        console.log(item.id);
        deleteItem(item._id);
        console.log("deleteItem");
      };

      const edit = (item) => {
          console.log(item._id)
          setToggle((prev) => !prev);
      } 

    return (
        <>
       { toggle ? (
           <div>
                <tr id="tr" key={index}>
                    <td id="td-id">{index}</td>
                    <td id="td-title">{item.title}</td>
                    <td id="td-price">{item.price}</td>
                    <td id="td-quantity">{item.quantity}</td>
                    <td id="td-description">{item.description}</td>
                    <td id="td-image">
                    <img id="td-image-tag" src={item.image} alt={item.title} />
                    </td>
                    <div>
                    <button id="td-delete-button" onClick={() => remove(item)}>X</button>
                    <button id="td-edit-button" onClick={() => edit(item)}>Edit</button>
                    </div>
                </tr>
           </div>
        )
        :
        (
            <div>
                <h1>edit here</h1>
                <button id="td-edit-button" onClick={() => submit(item)}>Edit</button>
            </div>
        )}
        </>
    )
}
