import React, { useState, useContext } from "react";
import { PostProviderContext } from "../context/PostProvider";
import { UserAuthContext } from "../context/UserAuthProvider";

export const CommentForm = (props) => {
  const { User: {_id }} = useContext(UserAuthContext)
  const { postId, commentDescription, } = props;
  console.log("post page Id ", postId)
  console.log("user Id", _id)

  const initInputs = {
    commentDescription: commentDescription || "",
  };

  const [inputs, setInputs] = useState(initInputs);
  const { addComment, editComment } = useContext(PostProviderContext);

console.log("inputs on CommentForm", inputs)


  function handleChange(e) {
    const { name, value } = e.target;
    setInputs((prevInputs) => ({
      ...prevInputs,
      [name]: value,
    }));
    // console.log("handleChange", inputs)
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    addComment(postId, inputs);
    editComment(postId, inputs)
    setInputs(initInputs);
  };

  return (
    <div>
      <form onSubmit={handleSubmit} onChange={handleChange}>
        <label>
          <input
            type="text"
            name="commentDescription"
            placeholder="add comment"
          />
        </label>
        <button onClick={handleSubmit}>{props.btnText}</button>
      </form>
    </div>
  );
};
