    const { posts, getAllPosts} = useContext(PostProviderContext)

    const { getCommentsById } = useContext(PostProviderContext)

  function get(){
      getCommentsById(_id)
  }

    useEffect(() => {
           getAllPosts("all Posts")
    }, []);

    return (
        <>
            <Nav2 />
            <div className="feedCont">
                { posts.map(post => <Post { ...post} key={post._id}/> ) }
            </div>
        </>
    )
}
