import React, { useContext } from 'react'
// import { UserAuthContext } from "../context/UserAuthProvider";
import { PostProviderContext } from '../context/PostProvider'
import { Post } from "./Post"

export const Search = () => {


const { posts, getAllPosts, searchByCamera, searchByLens, searchByStyle } = useContext(PostProviderContext)

function handleChange(e){
    styleFilter()
    cameraFilter()
    lensFilter()

}

function styleFilter(e){
    if(e.target === "all"){
        getAllPosts()
    } else {
        searchByStyle()
    }
}

function cameraFilter(e){
    if(e.target.value === "all"){
        getAllPosts()
    } else {
        searchByCamera(e.target.value)
    }
}

function lensFilter(e){
    if(e.target.value === "all"){
    } else {
        searchByLens(e.target.value)
    }
}

    return (
<div className="postsFeed">     
            <h3>Camera brand</h3>
                <select onChange={handleChange} className="filter">
                    <option value= "all">All Posts</option>
                    <option value="Canon">Canon</option> 
                    <option value="Sony">Sony</option>
                    <option value= "Nikon">Nikon</option>
                    <option value="Fujifilm">Fujifilm</option> 
                    <option value="Olympus">Olympus</option>
                    <option value= "Panasonic">Panasonic</option>
                    <option value="Leica">Leica</option> 
                    <option value="Hassleblad">Hassleblad</option>
                    <option value= "Iphone">Iphone</option>
                    <option value="Android">Android</option> 
                    <option value="Other">Other</option>
                </select>
            <h3>Camera lens</h3>
                <select onChange={handleChange} className="filter">
                    <option value= "all">All Posts</option>
                    <option value="Canon">Canon</option> 
                    <option value="Sony">Sony</option>
                    <option value= "Nikon">Nikon</option>
                    <option value="Fujifilm">Fujifilm</option> 
                    <option value="Olympus">Olympus</option>
                    <option value= "Panasonic">Panasonic</option>
                    <option value="Leica">Leica</option> 
                    <option value="Hassleblad">Hassleblad</option>
                    <option value= "Iphone">Iphone</option>
                    <option value="Android">Android</option> 
                    <option value="Other">Other</option>
                </select>
                <h3>Style</h3>
                <select onChange={handleChange} name="style">
                    <option input="">Style</option> 
                    <option value="Architectural">Architectural</option>
                    <option value="Astro">Astro</option> 
                    <option value="Car">Car</option>
                    <option input="Fashion">Fashion</option> 
                    <option value="Food">Food</option>
                    <option value="Landscape">Landscape</option>
                    <option value="Portrait">Portrait</option>
                    <option value="Macro">Macro</option> 
					<option value="Nature">Nature</option>
                    <option value="Night">Night</option>
                    <option value="Other">Other</option> 
                    <option value="Pet">Pet</option> 
                    <option value="Photojournalism">Photojournalism</option>
                    <option value="Product">Product</option>
                    <option value="Sports">Sports</option>
                    <option value="Still Life">Still Life</option>
                    <option value="Street">Street</option>
                    <option value="Travel">Travel</option>
                    <option value="Underwater">Underwater</option> 
                    <option value="Wedding">Wedding</option> 
                    <option value="Wildlife">Wildlife</option> 
                </select>

                { posts.map(post => <Post { ...post} key={post._id}/> ) }
        </div>
    )
}
