}

    // const { comments } = useContext(PostProviderContext)
    const { _id, getCommentsById, comments } = useContext(PostProviderContext)
    const [toggleComments, setToggleComments ] = useState(false)

    function showComments(){
            getComments(_id)
            handleToggle()
    }



    function handleToggle(){
        setToggleComments(prev => !prev)
    }


    return (
        <>
            {
                !toggleComments ?
                <div>
                    <h1>
                        <button onClick={showComments}>
                            View Comments
                        </button>
                    </h1>
                </div>
            :
                <div onClick={handleToggle}>
                    <h1>{comments}</h1>
                </div>
            }
        </>
    )
}
