import React, { useState } from "react";
import { CommentForm } from "./CommentForm"
// import { UserAuthContext } from "../context/UserAuthProvider"

export const Comment = (props) => {
  // const { user: _id } = useContext(UserAuthContext)
  const { commentDescription, deleteComment, editComment, _id } = props;

  const initInputs = {
    commentDescription: commentDescription || "",
  };

  const [inputs, setInputs] = useState(initInputs);
  const [toggle, setToggle ] = useState(false)

  function togglePage(){
    setToggle(prev => !prev)
  }

  function deleteCom() {
    deleteComment(_id);
  }

  function editCom() {
    editComment(_id);
    setInputs(inputs);
  }

  return (
    <>
    {
      !toggle ?
    <div className="comment" onClick={togglePage}>
      <h3>{commentDescription}</h3>

    </div>
      :
      <div className="comment" onClick={togglePage}>
        <CommentForm btnText="Submit Edit" commentDescription={commentDescription} />
    </div>
    }
    </>
  );
};
