import React, { useState } from "react";
import { CommentForm } from "./CommentForm"

export const Comment = (props) => {
  const { commentDescription, _id, deleteComment, commentId, handleChange } = props;

  const [toggle, setToggle ] = useState(false)

  console.log("commment Id", commentId)

  function deleteCom() {
    deleteComment(_id);
  }


  return (
    <>
    {
      !toggle ?
    <div className="comment">
      <h3>{commentDescription}</h3>
      <button onClick={() => setToggle(prev => !prev)}>Edit</button>
    </div>
      :
      <div className="comment">
        <CommentForm btnText="Submit Edit" commentId={commentId} handleChange commentDescription={commentDescription}/>
          <button onClick={deleteCom}>Delete</button>
          <button onClick={() => setToggle(prev => !prev)}>Close</button>
    </div>
    }
    </>
  );
};
