import React, { useState } from "react";
// import { UserAuthContext } from "../context/UserAuthProvider"

export const Comment = (props) => {
  // const { user: _id } = useContext(UserAuthContext)
  const { commentDescription, deleteComment, editComment, _id } = props;

  const initInputs = {
    commentDescription: commentDescription || "",
  };

  const [inputs, setInputs] = useState(initInputs);

  function deleteCom() {
    deleteComment(_id);
  }

  function editCom() {
    editComment(_id);
    setInputs(inputs);
  }

  return (
    <div className="comment">
      <CommentForm _id={_id} commentDescription={commentDescription}/>
      <h3>{commentDescription}</h3>
      <button onClick={deleteCom}>Delete</button>
      <button onClick={editCom}>Edit</button>
      <hr></hr>
    </div>
  );
};
