import React, { useContext, useState }  from 'react'
import { UserAuthContext } from "../context/UserAuthProvider";
import { PostForm } from "./PostForm"

export const ProfilePost = (props) => {

const { user: { username } } = useContext(UserAuthContext)
const { _id, imgUrl , style, cameraBrand, cameraModel, lensBrand, lensModel, caption, iso, shutterSpeed, fStop, deletePost, editPost/* accessories */ } = props

const [Toggle, setToggle ] =useState(false) 

    return (
        <>
        {!Toggle ?
        <div className="posts">
                <h3>{ username }</h3>
                <img src={ imgUrl } alt="post img" width="200" height="150"></img>
                <p>Settings: ùëì { fStop }, SS: { shutterSpeed }, ISO{ iso }</p>
                <p>{caption}</p>
                <p> Style: { style } </p>
                    <p>
                    Kit: 
                    <br></br>
                    Camera: {cameraBrand}, {cameraModel}
                        <br></br>
                    Lens: {lensBrand}, {lensModel}
                        <br></br>
                        {/* accessories: { accessories } */}
                    </p>
                <button onClick={() => props.deletePost(props._id) }>Remove Post</button>
                 <button onClick={() => setToggle(prevToggle => !prevToggle) }>Edit Post</button>
                {/* <button onClick={() => props.editPost(props._id) }>Edit Post</button> */}
                <hr></hr>
                <br></br>
        </div>
        :
        <div className="posts">
        <EditPostForm id={_id} />
                {/* <h3>{ username }</h3> */}
                {/* <img src={ imgUrl } alt="post img" width="200" height="150"></img> */}
                {/* <p>Settings: ùëì { fStop }, SS: { shutterSpeed }, ISO{ iso }</p> */}
                {/* <p>{caption}</p> */}
                {/* <p> Style: { style } </p> */}
                    <p>
                    Kit: 
                    <br></br>
                    Camera: {cameraBrand}, {cameraModel}
                        <br></br>
                    Lens: {lensBrand}, {lensModel}
                        <br></br>
                        {/* accessories: { accessories } */}
                    </p>
                <button onClick={() => deletePost(_id) }>Remove Post</button>
                <button onClick={() => editPost(_id) }>Edit Post</button>
                <button
                    onClick={ () => setToggle(prevToggle => !prevToggle)}>Close</button>
        </div>
    }
    </>
    )
}
