import React, { useContext, useState } from 'react'
import { PostProviderContext } from "../context/PostProvider"

export const TestPost = () => {

    const { newPost } = useContext(PostProviderContext)


    const [ inputs, setInputs ] = useState(options)

    function handleChange(e){
        const {name, value} = e.target
        setInputs(prevInputs => ({
            ...prevInputs,
            [name]: value
        }))
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        newPost(inputs)
        setInputs(options)
        window.location.reload(false)
        }

        const [ selected, setSelected ] = useState(null)

        const changeSelectOptionHandler = (e) => {
            setSelected(e.target.value)
        }

        const camera = ["Canon", "Sony", "Nikon", "Fujifilm", "Olympus", "Panasonic", "Leica", "Hassleblad", "Iphone", "Android", "Other"];

        const lensBrand = ["35mm", "50mm", "85mm"]; 
        const style = ["Portrait", "Car", "Landscape", "Fashion"];


        let type = null
        let options = null


        if (selected === "Camera") { 
            type = camera; 
          } else if (selected === "Lens") { 
            type = lensBrand; 
          } else if (selected === "Style") { 
            type = style; 
          } 

          if (type) { 
            options = type.map((choice) => <option key={choice}>{choice}</option>); 
          } 


    return ( 
        <div 
          style={{ 
            padding: "16px", 
            margin: "16px", 
          }} 
        > 
          <form onSubmit={ handleSubmit } onChange={handleChange} > 
            <div> 
              {/** Bind changeSelectOptionHandler to onChange method of select. 
               * This method will trigger every time different 
               * option is selected. 
               */} 
              <select onChange={changeSelectOptionHandler}> 
                <option>Choose...</option> 
                <option>Camera</option> 
                <option>Lens</option> 
                <option>Style</option> 
              </select> 
            </div> 
            <div> 
              <select> 
                { 
                  /** This is where we have used our options variable */
                  options 
                } 
              </select> 
              <button>Post</button>
            </div> 
          </form> 
        </div> 
    )
 }
