import React, { useState, useContext, useEffect } from "react";
import "./grid.css";
import { Container, Table } from "react-bootstrap";
import { ItemProviderContext } from "../../context.js/itemProvider";
import { Item } from "./Item";

export const Grid = () => {
  const { items, getAllItems, newItem, editItem, deleteItem } = useContext(
    ItemProviderContext
  );

  useEffect(() => {
    getAllItems();
  }, []);

  // const [data, setData ] = useState(items)
  // const [sortType, setSortType] = useState('titleA-Z');
  // useEffect(() => {
  //   const sortArray = type => {
  //     const types = {
  //       title: "titleA-Z" || "titleZ-A",
  //       price: "priceHigh" || "priceLow"
  //     };
  //     const sortProperty = types[type];
  //     const sorted = [...items].sort((a,b) => b[sortProperty] - a[sortProperty])
  //     setData(sorted)
  //   };
  //   sortArray(sortType)
  //   // getAllItems();
  // }, [sortType]);

  const initInputs = {
    title: "",
    description: "",
    price: "",
    quantity: "",
    image: "",
  };

  const [inputs, setInputs] = useState(initInputs);

  function onChangeItem(e) {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
  }

  const add = (e) => {
    e.preventDefault();
    newItem(inputs);
    setInputs(initInputs);
  };

  const [selected, setSelected] = useState("");

  const handleSelect = (e) => {
    const { name, value } = e.target;
    setSelected({
      [name]: value,
    });
  };

  // const sortedData = items.sort((a,b){
  //   return
  // })

  const sortedData = items.sort(function (a, b) {
    console.log("selected", selected)
    if (selected === "") {
      return items;
    }
    if (selected === "titleA-Z") {
      if (a.title.toLowerCase() < b.title.toLowerCase()) {
        return -1;
      } else if (a.title.toLowerCase() > b.title.toLowerCase()) {
        return 1;
      } return 0;
    }
    if (selected === "titleZ-A") {
      if (b.title.toLowerCase() < add.title.toLowerCase()) {
        return 1;
      } else if (b.title.toLowerCase() > a.title.toLowerCase()) {
        return -1;
      } return 0;
    }
    if (selected === "priceLow") {
      return a.price.toLowerCase() - b.price.toLowerCase()
    }
    if (selected === "priceHigh") {
      return b.price.toLowerCase() - a.price.toLowerCase();
    }
  });
  console.log(sortedData);
  // return sortedData

  // const renderItem = (item, index, _id) => {
  //     index = index + 1;
  //     return (
  //       <div>
  //         <Item index={index} item={item} id={_id} edit={editItem} delete={deleteItem} />
  //       </div>
  //     );
  // };

  return (
    <div id="grid-container">
      <form id="form">
        <div id="form-inputs-div">
          <input
            onChange={onChangeItem}
            type="text"
            name="title"
            placeholder="Title"
            className="input"
          />
          <input
            onChange={onChangeItem}
            type="text"
            name="description"
            placeholder="Description"
            className="input"
          />
          <input
            onChange={onChangeItem}
            type="number"
            name="price"
            placeholder="Price"
            className="input"
          />
          <input
            onChange={onChangeItem}
            type="number"
            name="quantity"
            placeholder="Quantity"
            className="input"
          />
          <input
            onChange={onChangeItem}
            type="text"
            name="image"
            placeholder="Image Url"
            className="input"
          />
          <button class="input" onClick={add}>
            Add Item
          </button>
        </div>
      </form>

      <Container id="container">
        <Table id="table">
          <thead id="thead">
            <div id="form-action-div">
              {/* <select id="sort-by" onClick={handleSelect}> */}
              <select id="sort-by" onChange={() => handleSelect()}>
                <option input="">
                  Sort by
                </option>
                <option value="titleA-Z">
                  Title A-Z
                </option>
                <option value="titleZ-A">
                  Title Z-A
                </option>
                <option value="priceLow">
                  Price Low
                </option>
                <option value="priceHigh">
                  Price High
                </option>
              </select>
            </div>
            <tr id="tr-header">
              <th id="th-id">ID</th>
              <th id="th-title">Title</th>
              <th id="th-price">Price</th>
              <th id="th-quantity">Quantity</th>
              <th id="th-description">Description</th>
              <th id="th-image">Image</th>
            </tr>
          </thead>
          <thbody id="th-body">
            {sortedData.map((item, index, _id) => (
              <Item
                index={index}
                item={item}
                id={_id}
                edit={editItem}
                delete={deleteItem}
              />
            ))}
          </thbody>
        </Table>
      </Container>
    </div>
  );
};
