import React, { useContext } from 'react'
import { PostProviderContext } from '../context/PostProvider'
import { Post } from "./Post"

export const Feed = (props) => {

    const { posts, getAllPosts, getType } = useContext(PostProviderContext)

function handleFilter(e){
    if(e.target.value === "all"){
        getAllPosts()
    } else {
        getType(e.target.value)
    }
}

    return (
        <div className="postsFeed">     
            <h3>Filter posts</h3>
                <select onChange={handleFilter} className="filter">
                    <option value= "all">All Bountys</option>
                    <option value="camera">Canon</option> 
                    <option value="camera">Jedi</option>
                </select>
                { posts.map(post => <Post { ...post} key={post._id}/> ) }
        </div>
    )
}
