export const Comment = (props) => {
  // const { user: _id } = useContext(UserAuthContext)
  const { commentDescription } = props;

  const initInputs = {
    commentDescription: commentDescription || "",
  };

  useEffect(() =>{
    setInputs(initInputs)
  },[])

  const [inputs, setInputs] = useState(initInputs);
  const [toggle, setToggle ] = useState(false)

  // function togglePage(){
  //   setToggle(prev => !prev)
  // }

  // function deleteCom() {
  //   deleteComment(_id);
  // }

  // function editCom() {
  //   editComment(_id);
  //   setInputs(inputs);
  // }

  return (
    <>
    {
      !toggle ?
    <div className="comment">
      <h3>{commentDescription}</h3>
      <button onClick={() => setToggle(prev => !prev)}>Edit</button>
    </div>
      :
      <div className="comment">
        <CommentForm btnText="Submit Edit" commentDescription={commentDescription} />
        <button onClick={() => setToggle(prev => !prev)}>Close</button>
    </div>
    }
    </>
  );
};
