import React, { useContext, useState } from "react";
import { PostProviderContext } from "../../context/PostProvider";
import { Comment } from "./comment/Comment";
import { CommentForm } from "./comment/CommentForm";

export const Post = (props) => {
  const {
    getCommentsById,
    comments,
    deleteComment,
    editComment,
    addPostLike,
    addComment,
  } = useContext(PostProviderContext);

  const {
    postId,
    imgUrl,
    style,
    cameraBrand,
    cameraModel,
    lensBrand,
    lensModel,
    caption,
    iso,
    shutterSpeed,
    fStop,
    username
    /* accessories */
  } = props;

  const [toggle, setToggle] = useState(false);

  function get() {
    setToggle((prev) => !prev);
    getCommentsById(postId);
    console.log("postId ", postId);
  }

  const commentFilter = comments.filter((comment) => comment.post === postId);

  return (
    <div className="post">
      {!toggle ? (
        <div className="post2">
          <h2>{username}</h2>
          <img src={imgUrl} alt="post img" onDoubleClick={addPostLike}></img>
            <li>Settings: ùëì {fStop}, SS: {shutterSpeed}, ISO: {iso}</li>
            <li>{cameraBrand} {cameraModel} & {lensBrand} {lensModel}</li>

          <ul>
            <li>{caption}</li>
          </ul>
            <button onClick={get}>Comments</button>
        </div>
      ) : (
        <>
          <br></br>
          <h2 className="username">{username}</h2>
          <img src={imgUrl} alt="post img"></img>
          <ul>
            <li>
              <p>{caption}</p>
            </li>
            <li>{style}</li>
            <li>
              Settings: ùëì {fStop}, SS: {shutterSpeed}, ISO{iso}
            </li>
            <li>
              Camera: {cameraBrand}, {cameraModel}
              <br></br>
              Lens: {lensBrand}, {lensModel}
              <br></br>
              {/* accessories: { accessories } */}
            </li>
          </ul>

          {commentFilter.map((comment) => (
            <Comment
              {...comment}
              key={comment._id}
              commentId={comment._id}
              deleteComment={deleteComment}
              editComment={editComment}
            />
          ))}

          <CommentForm
            postId={postId}
            submit={addComment}
            btnText="add Comment"
          />
          <button onClick={() => setToggle((prev) => !prev)}>
            Hide Comments
          </button>
        </>
      )}
    </div>
  );
};
