import React, { useContext } from "react";
import { PostProviderContext } from "../context/PostProvider";
import { Comment } from "./Comment";
// import { UserAuthContext } from "../context/UserAuthProvider";

export const Post = (props) => {
  const {
    _id,
    imgUrl,
    style,
    cameraBrand,
    cameraModel,
    lensBrand,
    lensModel,
    caption,
    iso,
    shutterSpeed,
    fStop,
    /* accessories */
  } = props;

  const {getCommentsById} = useContext(PostProviderContext)

  function get(){
      getCommentsById(_id)
  }

  const { comments } = useContext(PostProviderContext)
  console.log("comments", comments)
  console.log("post _id", _id)
  return (
    <>
      <div className="post">
        <br></br>

        <img src={imgUrl} alt="post img"></img>

        <ul>
          <li>
            <p>{caption}</p>
          </li>
          <li>{style}</li>
          <li>
            Settings: ùëì {fStop}, SS: {shutterSpeed}, ISO{iso}
          </li>
          <li>
            Camera: {cameraBrand}, {cameraModel}
            <br></br>
            Lens: {lensBrand}, {lensModel}
            <br></br>
            {/* accessories: { accessories } */}
          </li>
        </ul>
        <div onClick={get}>
            Show Comments
            <ul>

            </ul>
            {comments.map(comment => <Comment {...comment} key={_id} />)}
        </div>
      </div>
    </>
  );
};
