import React, { useContext, useState } from 'react'
import { UserAuthContext } from "../context/UserAuthProvider"
import { PostProviderContext } from "../context/PostProvider"
import { EditProfile } from "./EditProfile"
import { Post } from "./Post"

export const Profile = (props) => {


    const { user: { username, bio, email, profilePicture }, logout } = useContext(UserAuthContext)
    const { posts, userPosts } = useContext(PostProviderContext)

    const [toggle, setToggle ] = useState(false)
    function toggleForm(){
        setToggle (prev => !prev)
    }

    function filterPosts(id, user){
        const profilePosts = posts.map(post => {
            if(_id === user._id){
                return {...post, }
            }
        }) 
    }

    return (
        <div className="profilePage">
                {
                    !toggle ? 
                        <div className="profileBox">
                            <div className="profileContainer">
                            <br></br>
                            <img src={ profilePicture } alt="Profile" width="150" height="150"></img>
                                <div className="profileInfo">
                                        <h1>{ username }</h1>
                                        <h4>{ bio }</h4>
                                        <h4>{ email } </h4>
                                </div>
                            <div className="profilePagePosts">
                                { posts.map(post => <Post { ...post} key={post._id}/> ) }
                            </div>
                            </div>
                                <button onClick={ toggleForm }>Edit Profile</button>
                                <br></br>
                                <button onClick={ logout }> Signout </button>
                            </div>

                    :

                    <div className="profileEdit">
                            <EditProfile />
                            <button onClick={ toggleForm }>Discard Changes</button>
                    </div>

                }
        </div>
    )
}
