export const ItemEdit = (props) => {

    const [inputs, setInputs] = useState()

    function onChangeItem(e) {
        setInputs({
            ...inputs, 
            [e.target.name]: e.target.value
        })
    }

    return (
        <div>
            <form id="form">
              <div id="form-inputs-div">
                      <input onChange={onChangeItem} type="text" name="title" placeholder={props.item.title} className="input"/>
                      <input onChange={onChangeItem} type="text" name="description" placeholder={props.item.description} className="input"/>
                      <input onChange={onChangeItem} type="number" name="price" placeholder={props.item.price} className="input"/>
                      <input onChange={onChangeItem} type="number" name="quantity" placeholder={props.item.quantity} className="input"/>
