import React, { useContext, useState, useEffect } from "react";
import { UserAuthContext } from "../../context/UserAuthProvider";
import { PostProviderContext } from "../../context/PostProvider";
import { ProfilePost } from "../profile/ProfilePost";
import { PostBar } from "../posts/PostBar";
import { Nav } from "../nav/Nav"

export const Profile = (props) => {
  const {
    user: { username, bio, email, profilePicture, _id },
    logout,
    editUserProfile,
  } = useContext(UserAuthContext);

  const { userPosts, getUserPosts, deletePost, editPost } = useContext(
    PostProviderContext
  );

  const initInputs = {
    profilePicture: profilePicture || "",
    bio: bio || "",
    email: email || "",
  };

  // console.log("userPosts", userPosts)
  const [inputs, setInputs] = useState(initInputs);
  const [toggle, setToggle] = useState(false);

  function toggleForm(e) {
    setToggle((prev) => !prev);
  }

  useEffect(() => {
    getUserPosts(_id);
  }, []);

  function handleChange(e) {
    const { name, value } = e.target;
    setInputs((prevInputs) => ({
      ...prevInputs,
      [name]: value,
    }));
  }

  function handleSubmit(e) {
    e.preventDefault();
    editUserProfile(inputs, _id);
    getUserPosts(_id);
    // logout()
  }

  return (
    <div className="appCont">
          <PostBar />
        <div div className="profile">
      {!toggle ? (
        <div>
          <img
            src={profilePicture}
            alt="Profile-Pic"
            // width="150"
            // height="150"
          ></img>
          <h1>{username}</h1>
          <h4>{bio}</h4>
          <h4>{email} </h4>
          <button onClick={toggleForm} className="buttonSkinny">
            Profile Settings
          </button>
            {userPosts.map((post) => (
              <ProfilePost
                {...post}
                key={post._id}
                deletePost={deletePost}
                editPost={editPost}
              />
            ))}
        </div>
      ) : (
        <div className="profileEdit">
          <img
            src={profilePicture}
            alt="Profile"
            placeholder="Profile Pic"
          ></img>
          <>
            <h1>{username}</h1>
            <h4>{bio}</h4>
            <h4>{email} </h4>
          </>
          {/* <button onClick={toggleForm} className="button">Edit Profile</button> */}
          <form>
            <label>
              <input
                onChange={handleChange}
                type="text"
                name="profilePicture"
                placeholder={profilePicture || "Add profile picture"}
              />
              <input
                onChange={handleChange}
                type="text"
                name="bio"
                placeholder={bio || "About me"}
              />
              <input
                onChange={handleChange}
                type="text"
                name="email"
                placeholder={email || "Email"}
              />
            </label>
            <br></br>
            <button onClick={handleSubmit} className="button">
              Save Changes
            </button>
          </form>
          <button onClick={toggleForm} className="button">
            Back
          </button>
          <button onClick={logout} className="button">
            {" "}
            Signout{" "}
          </button>
        </div>
      )}
      </div>
          <Nav />
    </div>
  );
};
