import React, { useState } from "react";
import { Signup } from "./Signup";
import { Login } from "./Login";

export const Auth = () => {
  const [toggle, setToggle] = useState(true);

  const toggleForm = () => {
    setToggle((prev) => !prev);
  };

  return (
    <>
      {!toggle ? (
        <div>
          <h1>Please Login</h1>
          <form onSubmit={handleSubmit} onChange={handleChange}>
            <label>
              <br></br>
              <h1>Please login</h1>
              <br></br>
              <br></br>
              <h1>Username:</h1>
              <input
                type="text"
                name="username"
                placeholder={inputs.username}
                className="input2"
              />
              <h1>Password:</h1>
              <input
                type="password"
                name="password"
                placeholder={inputs.password}
                className="input2"
              />
            </label>
            <br></br>
            <button onClick={handleLogin} className="button">
              {" "}
              Login{" "}
            </button>
            {/* <p style={{ color: "red" }}>{errMsg}</p> */}
          </form>
          <button onClick={toggleForm}>Not yet a user</button>
        </div>
      ) : (
        <div>
          <h1>Signup</h1>
          <Signup />
          <button onClick={toggleForm}>Not yet a user</button>
        </div>
      )}
    </>
  );
};
